#!/usr/bin/env python3

# Leggi il file index.html
with open('/workspace/temp-repo/index.html', 'r', encoding='utf-8') as f:
    content = f.read()

# Nuovi loghi base64
new_desktop_logo = ""

new_mobile_logo = ""

# Sostituisci logo desktop
import re
desktop_pattern = r'<img src="data:image/svg\+xml;base64,[^"]*?alt="Aphrodite">'
desktop_replacement = f'<img src="data:image/svg+xml;base64,{new_desktop_logo}" alt="Aphrodite">'
content = re.sub(desktop_pattern, desktop_replacement, content)

# Sostituisci logo mobile
mobile_pattern = r'<img src="data:image/svg\+xml;base64,[^"]*?alt="Aphrodite">'
mobile_replacement = f'<img src="data:image/svg+xml;base64,{new_mobile_logo}" alt="Aphrodite">'
content = re.sub(mobile_pattern, mobile_replacement, content)

# Salva il file modificato
with open('/workspace/temp-repo/index.html', 'w', encoding='utf-8') as f:
    f.write(content)

print("Loghi aggiornati con successo!")